import React, {useState} from 'react';
import './stylesheet.css';
import './reset.css';

const api = {
  key:"7248eff058ba1f79c53e30c2ba0fac6e",
  base:"https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [coord, setCoord] = useState('');
  const [weatherInfo, setWeatherInfo] = useState('');


  const FetchLonLat = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&cnt=1&appid=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setCoord(result);
          setQuery('');
          console.log(result);
          fetchWeatherInfo(result);
        });

    }
  }

  function fetchWeatherInfo(coordRes) {
    var lat = String(coordRes.coord.lat);
    var lon = coordRes.coord.lon;
    fetch(`${api.base}onecall?lat=51.5085&lon=${lon}&appid=${api.key}`)
      .then(res => res.json())
      .then(result2 => {
        setWeatherInfo(result2);
        console.log(result2);
      });
  }

  const inputHandler = (event) => {
    setQuery(event.target.value)
  }

  return (
    <div className="app">
      <main>
        <div className="searchBox">
          <input
            type="text"
            className="searchBoxInput"
            placeholder="Enter destination..."
            onChange={inputHandler}
            value={query}
            onKeyPress={FetchLonLat}
          />
        </div>
        {(typeof coord.main != "undefined") ? (
          <div>
            <div className="weather">
              <div className="temperature">
                <p>{coord.coord.lat}, {coord.coord.lon}</p>
              </div>
            </div>
            <div className="hourlyForecast">
              <table>
                <tr className="center">
                  <td>Now</td>
                  <td className="leftBorder">18</td>
                  <td className="leftBorder">19</td>
                  <td className="leftBorder">20</td>
                </tr>
                <tr className="center">
                  <td>11째</td>
                  <td className="leftBorder">8째</td>
                  <td className="leftBorder">6째</td>
                  <td className="leftBorder">5째</td>
                </tr>
                <tr className="center">
                  <td>2%</td>
                  <td className="leftBorder">0%</td>
                  <td className="leftBorder">0%</td>
                  <td className="leftBorder">0%</td>
                </tr>
              </table>
            </div>
            <div className="rainhumidity">
              <table>
                <tr>
                  <td>Chance of rain</td>
                  <td>Humidity</td>
                </tr>
                <tr>
                  <td className="bigFont">2%</td>
                  <td className="bigFont">62%</td>
                </tr>
              </table>
            </div>
            <div className="sunsetrise">
              <table>
                <tr>
                  <td>Sunrise</td>
                  <td>Sunset</td>
                </tr>
                <tr>
                  <td className="bigFont">06:49</td>
                  <td className="bigFont">17:37</td>
                </tr>
              </table>
            </div>
            <div className="windpressure">
              <table>
                <tr>
                  <td>Wind</td>
                  <td>Pressure</td>
                </tr>
                <tr>
                  <td className="bigFont">2 mph</td>
                  <td className="bigFont">1041</td>
                </tr>
              </table>
            </div>
          </div>
        ) : 
        (
          <div>
            <div className="enterDestination">
              Enter destination to get weather information
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
