import React, {useState} from 'react';
import './stylesheet.css';
import './reset.css';

const api = {
  key:"7248eff058ba1f79c53e30c2ba0fac6e",
  base:"https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState('');


  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }

  const icon_id = weather.weather.icon
  const iconURL = "http://openweathermap.org/img/wn/${icon_id}@2x.png"

  return (
    <div className="app">
      <main>
        <div className="searchBox">
          <input
            type="text"
            className="searchBoxInput"
            placeholder="Enter destination..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {(typeof weather.main != "undefined") ? (
          <div>
            <div className="weather">
              <div className="temperature">
                <p>{weather.name}, {weather.sys.country}</p>
                {Math.round(weather.main.temp)}°c
                <p>hi: {Math.round(weather.main.temp_max)}° lo: {Math.round(weather.main.temp_min)}°</p>
              </div>
              <div className="weatherDesc">
                {weather.weather[0].description}, <img src='http://openweathermap.org/img/wn/{weather}@2x.png'/>
              </div>
            </div>
            <div className="hourlyForecast">
              <table>
                <tr className="center">
                  <td>Now</td>
                  <td className="leftBorder">22</td>
                  <td className="leftBorder">23</td>
                  <td className="leftBorder">00</td>
                </tr>
                <tr className="center">
                  <td>11°</td>
                  <td className="leftBorder">10°</td>
                  <td className="leftBorder">10°</td>
                  <td className="leftBorder">10°</td>
                </tr>
                <tr className="center">
                  <td>10%</td>
                  <td className="leftBorder">10%</td>
                  <td className="leftBorder">10%</td>
                  <td className="leftBorder">10%</td>
                </tr>
              </table>
            </div>
          </div>
        ) : 
        (
          <div>
            Enter destination to get weather information
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
