import React, {useState} from 'react';
import './stylesheet.css';
import './reset.css';

const api = {
  key:"7248eff058ba1f79c53e30c2ba0fac6e",
  base:"https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState('');


  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }

  return (
    <div className="app">
      <main>
        <div className="searchBox">
          <input
            type="text"
            className="searchBoxInput"
            placeholder="Enter destination..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {(typeof weather.main != "undefined") ? (
          <div>
            <div className="weather">
              <div className="temperature">
                <p>{weather.name}, {weather.sys.country}</p>
                {Math.round(weather.main.temp)}°c
                <p>hi: 13°c lo: 12°c</p>
              </div>
              <div className="weatherDesc">
                {weather.weather[0].description} <i class="fas fa-cloud-rain"></i>
              </div>
            </div>
            <div className="LocationDate">
              <div className="location">
                {weather.name}, {weather.sys.country} 
              </div>
            </div>
            <div className="hourlyForecast">
              <select className ="forecastOptions">
                <option className="options">1 hour</option>
                <option className="options">2 hour</option>
                <option className="options">3 hour</option>
                <option className="options">4 hour</option>
              </select>
              <p>1500 | 25°c | 0%&emsp;<i class="fas fa-cloud-rain"></i></p>
              <ul className="forecast">
                <li>1500 | 12°c | 98%&emsp;<i class="fas fa-cloud-rain"></i></li>
                <div className="solid"></div>
                <li>1600 | 11°c | 10%&emsp;<i class="fas fa-cloud"></i></li>
                <div className="solid"></div>
                <li>1700 | 11°c | 10%&emsp;<i class="fas fa-cloud"></i></li>
                <div className="solid"></div>
                <li>1800 | 11°c | 10%&emsp;<i class="fas fa-cloud"></i></li>
                <div className="solid"></div>
              </ul>
            </div>
          </div>
        ) : 
        (
          <div>
            Enter destination to get weather information
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
