import React, {useState} from 'react';
import './stylesheet.css';
import './reset.css';

const api = {
  key:"7248eff058ba1f79c53e30c2ba0fac6e",
  base:"https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState('');

  //`pro.openweathermap.org/data/2.5/forecast/hourly?q=${query}&units=metric&APPID=${api.key}`


  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}hourly?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }

  return (
    <div className="app">
      <main>
        <div className="searchBox">
          <input
            type="text"
            className="searchBoxInput"
            placeholder="Enter destination..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {(typeof weather.main != "undefined") ? (
          <div>
            <div className="weather">
              <div className="temperature">
                {weather.list.main.temp}Â°c
              </div>
              <div className="weatherDesc">
                {weather.weather[0].description}
              </div>
            </div>
            <div className="LocationDate">
              <div className="location">
                {weather.name}, {weather.sys.country} 
              </div>
            </div>
          </div>
        ) : 
        (
          <div>
            Enter destination to get weather information
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
